const scroll=document.querySelector(".scroll");function scrollToTop(){window.scrollTo({top:0,behavior:"smooth"})}window.addEventListener("scroll",()=>{scroll.classList.toggle("active",window.scrollY>200)}),scroll.addEventListener("click",()=>{scrollToTop()});const hamburger=document.querySelector(".hamburger"),menu=document.querySelector(".menu"),overlay=document.querySelector(".overlay"),closeElement=document.querySelector(".menu__close");function openMenu(){menu.classList.add("active"),overlay.classList.add("active")}function closeMenu(){menu.classList.remove("active"),overlay.classList.remove("active")}hamburger.addEventListener("click",()=>{openMenu()}),closeElement.addEventListener("click",()=>{closeMenu()});